import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import Link from '@mui/material/Link';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getEpisodesByChannel } from '../../redux/channels/actions/channels';
import IndividualEpisodeCard from '../episodes/IndividualEpisodeCard';
import { MAIN_LIGHT_YELLOW } from '../shared/Constant';

const EpisodeList = (props) => {
  const { channelName } = props;
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const channel = useSelector(state => state.channels[channelName]) || {};
  const episodes = channel.episodes || [];
  const thumbnail = channel.thumbnail;
  console.log(thumbnail);
  const publisher = channel.publisher;

  useEffect(() => {
    dispatch(getEpisodesByChannel(channelName));
  }, [channelName]);

  return (
    <div>
    <Grid container spacing={2} c>
      <Grid item xs={5} container>
        <Box
          sx={{
            display: 'flex',
            alignItems: 'center',
            marginBottom: '30px',
            position: 'fixed',
            padding: '1%',
            backgroundColor: MAIN_LIGHT_YELLOW,
          }}
        >
          <Link onClick={() => navigate(-1)} sx={{ cursor: 'pointer' }}>
            <ArrowBackIosIcon />
          </Link>
          <img src={thumbnail} />
          <Typography
            sx={{
              fontSize: '20px',
              fontWeight: 'bold',
              color: '#665A48',
            }}
          >
            {channelName}
          </Typography>
        </Box>
      </Grid>
      <Grid item xs={7}>
        <Grid container justifyContent="center">
          {episodes && Array.isArray(episodes) ? 
            episodes.map((ep) => (
              <Grid item xs={12}>
                <IndividualEpisodeCard episode={ep} />
              </Grid>
            )) :
            <Typography
              sx={{}}
            >
              {episodes}
            </Typography>
          }
        </Grid>
      </Grid>
    </Grid>
    </div>
  )
}

EpisodeList.propTypes = {
  channelName: PropTypes.string.isRequired,
};

export default EpisodeList
