import { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { authenticateUser } from '../../redux/auth/actions/auth';
import { getFavouriteList } from '../../redux/auth/actions/auth';

const dispatch = useDispatch();

const useAuthentication = () => {
  useEffect(() => {
    dispatch(authenticateUser());
  }, [dispatch]);
  const currUser = useSelector(state => state.auth.currUser);
  return currUser;
};

const useFavouriteList = () => {
  const currentUser = useAuthentication();
  dispatch(getFavouriteList(currentUser.id));
  const list = useSelector(state => state.auth.favouriteList);
  return list;
}

export default { useAuthentication, useFavouriteList}
