import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import KeyboardDoubleArrowDownIcon from '@mui/icons-material/KeyboardDoubleArrowDown';
import KeyboardDoubleArrowUpIcon from '@mui/icons-material/KeyboardDoubleArrowUp';
import IconButton from '@mui/material/IconButton';
import Button from '@mui/material/Button';
import ArrowRightIcon from '@mui/icons-material/ArrowRight';
import IndividualChannelCard from './IndividualChannelCard';

import { useDispatch } from 'react-redux';
import { createChannel } from '../actions/channels';

import ShortPodcastList from './ShortPodcastList';
const { Client } = require('podcast-api');

const client = Client({ apiKey: '6f228db1aec840ec96932ebdc92138e8' });

const GetBestChannelsByGenre = (props) => {
  const { genreId } = props;
  const [channels, setChannels] = useState([]);
  const [genre, setGenre] = useState("");
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchPodcastsByGenre = async () => {
      // client.fetchBestPodcasts({
      //   genre_id: genreId,
      //   region: 'us',
      //   sort: 'listen_score',
      //   safe_mode: 0,
      // }).then((response) => {
      //   const data = response.data;
      //   setChannels(data.podcasts);
      //   setGenre(data.name);
      // }).catch((error) => {
      //   console.log(error);
      // });
      const channels = [
        {title: 'Channel 1', description: 'This is channel 1 description'},
        {title: 'Channel 2', description: 'This is channel 2 description'},
        {title: 'Channel 3', description: 'This is channel 3 description'},
      ]
      setChannels(channels);
      setGenre('a genre');
    }

    fetchPodcastsByGenre();
  }, [genreId]);

  useEffect(() => {
    dispatch(createChannel());
  }, [dispatch]);

  return (
    <Grid>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <Typography sx={{ fontSize: '20px' }}>Best In {genre}</Typography>
        <IconButton>
          <ArrowRightIcon sx={{ fontSize: '40px' }} />
        </IconButton>
      </div>
      <ShortPodcastList channels={channels.slice(0,4)} />
    </Grid>
  );
};

GetBestChannelsByGenre.propTypes = {
  genreId: PropTypes.string.isRequired,
};

export default GetBestChannelsByGenre;
