const GenreReducer = (
  state = {
    genres: [],
    channels: [],
  },
  action = { type: '', payload: null },
) => {
  switch (action.type) {
    case 'GET_CHANNELS_BY_GENRE': {
      return {
        ...state,
        channels: action.payload,
      };
    }
    default:
      return state;
  }
};

export default GenreReducer;
