import React from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import FavoriteIcon from '@mui/icons-material/Favorite';
import AddCircleOutlineOutlinedIcon from '@mui/icons-material/AddCircleOutlineOutlined';
import { MAIN_DARK_BLUE, MAIN_GREEN, MAIN_LIGHT_YELLOW, MAIN_ORANGE } from '../shared/Constant.js';

const styles = {
  container: {
    position: 'relative',
    zIndex: 1,
  },
  root: {
    padding: '4%',
    marginBottom: '5%',
    backgroundColor: MAIN_LIGHT_YELLOW,
    width: '100%',
    border: ' 1px solid #3D405B',
    borderRadius: '5px',
  },
  img: {
    width: '80%',
    objectFit: 'cover',
    borderRadius: '5px'
  },
  title: {
    fontSize: '18px',
    color: MAIN_DARK_BLUE,
    fontWeight: 'bold'
  },
  heart: {
    color: MAIN_ORANGE,
    fontSize: '18px'
  },
  addbtn: {
    fontSize: '18px'
  },
  box: {
    backgroundColor: MAIN_ORANGE,
    position: 'absolute',
    left: '1%',
    top: '3%',
    width: '100%',
    height: '100%',
    zIndex: -1,
    borderRadius: '5px'
  },
  btnContainer: {
    marginTop: '10px'
  },
  button: {
    cursor: 'pointer',
  },
};

const IndividualEpisodeCard = (props) => {
  const { episode } = props;
  const { id, thumbnail, title } = episode;

  return (
    <div style={styles.container}>
      <Box sx={styles.box} />
      <Grid container sx={styles.root}>
        <Grid item xs={2}>
          <img src={thumbnail} style={styles.img} alt="" />
        </Grid>
        <Grid item xs={10}>
          <Typography sx={styles.title}>{title}</Typography>
          <span style={styles.button}>
            <FavoriteIcon style={styles.heart} />
          </span>
          <span>
            <AddCircleOutlineOutlinedIcon style={styles.addBtn}/>
          </span>
        </Grid>
        {/* <Grid item sx={styles.btnContainer}>
          <span onClick={handleHeartClick} style={styles.button}>
            {(heart || hearted) ? <FavoriteIcon style={styles.heart} /> : <FavoriteBorderIcon style={styles.heart} />}
          </span>
        </Grid> */}
      </Grid>
    </div>
  )
}

IndividualEpisodeCard.propTypes = {
  channelName: PropTypes.string.isRequired,
};

export default IndividualEpisodeCard
