import React from 'react';
import PropTypes from 'prop-types';

const styles = {
  container: {
    position: 'relative',
    zIndex: 1,
  },
  root: {
    padding: '10%',
    height: '30vh',
    marginBottom: '15%',
    backgroundColor: MAIN_LIGHT_YELLOW,
    width: '100%',
    border: ' 1px solid #3D405B',
    borderRadius: '5px'
  },
  img: {
    width: '18vh',
    objectFit: 'cover',
    borderRadius: '5px'
  },
  card: {
    textAlign: 'center',
  },
  titleContainer: {
    marginTop: '5px',
  },
  title: {
    fontSize: '18px',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
    color: MAIN_DARK_BLUE,
    fontWeight: 'bold'
  },
  button: {
    cursor: 'pointer',
  },
  heart: {
    color: MAIN_ORANGE,
    fontSize: '18px'
  },
  box: {
    backgroundColor: MAIN_GREEN,
    position: 'absolute',
    left: '3%',
    top: '3%',
    width: '100%',
    height: '100%',
    zIndex: -1,
    borderRadius: '5px'
  },
  btnContainer: {
    marginTop: '10px'
  }
};

const IndividualEpisodeCard = (props) => {
  const { episode } = props;
  const { id, thumbnail, title } = episode;

  return (
    <div style={styles.container}>
    <Link>
      <Box sx={styles.box} />
      <Grid sx={styles.root}>
        <Grid item sx={styles.card}>
          <img src={thumbnail} style={styles.img} alt="" />
        </Grid>
        <Grid item sx={styles.titleContainer}>
          <div style={styles.title}>{title}</div>
        </Grid>
        <Grid item sx={styles.btnContainer}>
          {/* <span onClick={handleHeartClick} style={styles.button}>
            {(heart || hearted) ? <FavoriteIcon style={styles.heart} /> : <FavoriteBorderIcon style={styles.heart} />}
          </span> */}
        </Grid>
      </Grid>
    </Link>
  </div>
  )
}

IndividualEpisodeCard.propTypes = {
  channelName: PropTypes.string.isRequired,
};

export default IndividualEpisodeCard
