import * as api from '../api/channels';

export const addHearToChannel = (channeId, userId) => async(dispatch) => {
  try {
    const { data } = await api.addHearToChannel(channeId, userId);
    dispatch({ type: 'ADD_HEART', payload: { channelId: true } })
  } catch (error) {
    console.log(error);
  }
};
