const initialState = {
  channels: [],
  favChannels: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_HEART': {
      const channelId = action.payload;
      return {
        ...state,
        [channelId]: true,
      }
    }
    case 'REMOVE_HEART': {
      const channelId = action.payload;
      return {
        ...state,
        [channelId]: false,
      }
    }
    case 'ADD_TO_FAV': {
      console.log(state);
      state.favChannels.push(action.payload);
      console.log(state);
      return {
        ...state,
      }
    }
    default:
      return state;
  }
}

export default reducer;
