import { useFavouriteList } from "./CurrentUser";
import { getFavouriteList } from '../redux/auth/actions/auth';
import { useSelector } from 'react-redux';

export const ValidateFavourite = (channelId) => {
  const list = useSelector(state => state.auth.favouriteList);
  const favouriteIds = list.map((item) => item.id);
  return favouriteIds.includes(channelId);
}
