import { useFavouriteList } from "./CurrentUser";

export const validateFavourite = (channelId) => {
  const list = useFavouriteList();
  const favouriteIds = list.map((item) => item.id);
  for (id in favouriteIds) {
    if (channelId == id) {
      return true;
    }
  }
  return false;
}
