const initialState = {
  genres: [],
  genre: { name: '', channels: [] },
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'GET_ALL_GENRES': {
      return {
        ...state,
        genres: action.payload,
      };
    }
    case 'GET_CHANNELS_BY_GENRE_NAME': {
      const { name, channels } = action.payload;
      return {
        ...state,
        genre: { name, channels }
      };
    }
    default:
      return state;
  }
};

export default reducer;
