import React from 'react';
import PropTypes from 'prop-types';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Chip from '@mui/material/Chip';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import { MAIN_ORANGE, MAIN_DARK_BLUE } from '../shared/Constant';

const ChannelOverview = (props) => {
  const { channel } = props;
  console.log(channel);

  return (
    <Grid container sx={{ marginBottom: '3%' }}>
      <Grid xs={3}><img src={channel.thumbnail} /></Grid>
      <Grid xs={9} sx={{ display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
        <div>
          <Typography
            sx={{
              fontSize: '40px',
              fontWeight: 'bold',
              color: MAIN_DARK_BLUE,
            }}
          >
            {channel.title}
          </Typography>
          <div style={{ display: 'flex', flexDirection: 'row', alignsItem: 'center' }}>
            <Chip
              label={channel.publisher}
              variant="outlined"
              size="large"
              sx={{ marginRight: '5%' }}
            />
            <span>
              {(channel.hearted) ? <FavoriteIcon sx={{ color: MAIN_ORANGE, fontSize: '25px' }} /> : <FavoriteBorderIcon sx={{ color: MAIN_ORANGE, fontSize: '25px' }} />}
            </span>
          </div>
        </div>
        <div>
          <Typography
            sx={{
              color: MAIN_DARK_BLUE,
              fontSize: '18px',
              fontWeight: 'bold'
            }}
          >
            About
          </Typography>
          <Typography
            sx={{
              width: '80%',
              color: MAIN_DARK_BLUE,
              fontSize: '18px'
            }}
          >
            {channel.description}
          </Typography>
        </div>
      </Grid>
    </Grid>
  );
}

ChannelOverview.propTypes = {
  channel: PropTypes.object.isRequired,
};

export default ChannelOverview;
