import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import Link from '@mui/material/Link';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getEpisodesByChannel } from '../../redux/channels/actions/channels';
import IndividualEpisodeCard from '../episodes/IndividualEpisodeCard';
import { MAIN_LIGHT_GREY } from '../shared/Constant';

const EpisodeList = (props) => {
  const { channelName } = props;
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const channel = useSelector(state => state.channels[channelName]) || {};
  const episodes = channel.episodes || [];
  const thumbnail = channel.thumbnail;
  const publisher = channel.publisher;

  useEffect(() => {
    dispatch(getEpisodesByChannel(channelName));
  }, [channelName]);

  return (
    <div>
    <Grid
      container
      sx={{
        marginBottom: '30px',
        padding: '3%',
        backgroundColor: MAIN_LIGHT_GREY,
      }}
    >
      <Grid item xs={12} container>
          <Grid xs={12}>
            <Link onClick={() => navigate(-1)} sx={{ cursor: 'pointer' }}>
              <ArrowBackIosIcon />
            </Link>
          </Grid>
          <Grid xs={6}><img src={thumbnail} /></Grid>
          <Grid xs={6}>
            <Typography
              sx={{
                fontSize: '50px',
                fontWeight: 'bold',
                color: '#665A48',
              }}
            >
              {channelName}
            </Typography>
          </Grid>
      </Grid>
      <Grid xs={12}>
        <Divider />
      </Grid>
      <Grid item xs={12}>
        <Grid container justifyContent="center">
          {episodes && Array.isArray(episodes) ? 
            episodes.map((ep) => (
              <Grid item xs={12}>
                <IndividualEpisodeCard episode={ep} />
              </Grid>
            )) :
            <Typography
              sx={{}}
            >
              {episodes}
            </Typography>
          }
        </Grid>
      </Grid>
    </Grid>
    </div>
  )
}

EpisodeList.propTypes = {
  channelName: PropTypes.string.isRequired,
};

export default EpisodeList
