import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import PlayArrowIcon from '@mui/icons-material/PlayArrow';
import IndividualChannelCard from './IndividualChannelCard';
import GetBestChannelsByGenreName from './GetBestChannelsByGenreName';

const ShortPodcastList = (props) => {
  const { genreName } = props;

  return (
    <Grid>
      <Link
        to={`/${genreName}`}
        style={{
          textDecoration: 'none',
          color: 'inherit',
        }}  
      >
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <Typography
            sx={{
              fontSize: '25px',
              marginBottom: '10px'
            }}
          >
            {genreName}
          </Typography>
          <PlayArrowIcon sx={{ fontSize: '40px' }}/>
        </div>
      </Link>
      <GetBestChannelsByGenreName genreName={genreName} short={true}/>
    </Grid>
  )
}

ShortPodcastList.propTypes = {
  channels: PropTypes.array.isRequired,
};


export default ShortPodcastList
