const initialState = {
  channels: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'GET_EPISODES_BY_CHANNEL': {
      const { title, episodes } = action.payload;
      return {
        ...state,
        [title]: episodes,
      }
    }
    default:
      return state;
  }
}

export default reducer;
