import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import KeyboardDoubleArrowDownIcon from '@mui/icons-material/KeyboardDoubleArrowDown';
import KeyboardDoubleArrowUpIcon from '@mui/icons-material/KeyboardDoubleArrowUp';
import IconButton from '@mui/material/IconButton';
import Button from '@mui/material/Button';
import ArrowRightIcon from '@mui/icons-material/ArrowRight';
import IndividualChannelCard from './IndividualChannelCard';

import { useDispatch } from 'react-redux';
import { createChannel } from '../actions/channels.js'

import ShortPodcastList from './ShortPodcastList';
const { Client } = require('podcast-api');

const client = Client({ apiKey: '6f228db1aec840ec96932ebdc92138e8' });

const GetBestChannelsByGenre = (props) => {
  const { genreId } = props;
  const [channels, setChannels] = useState([]);
  const [genre, setGenre] = useState("");
  const dispatch = useDispatch();
  const mockData = [
    {title: 'Channel 1', image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/b/b6/Image_created_with_a_mobile_phone.png/640px-Image_created_with_a_mobile_phone.png'},
    {title: 'Channel 2', image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/b/b6/Image_created_with_a_mobile_phone.png/640px-Image_created_with_a_mobile_phone.png'},
    {title: 'Channel 3', image: 'https://upload.wikimedia.org/wikipedia/commons/thumb/b/b6/Image_created_with_a_mobile_phone.png/640px-Image_created_with_a_mobile_phone.png'},
  ];
  useEffect(() => {
    const fetchPodcastsByGenre = async () => {
      client.fetchBestPodcasts({
        genre_id: genreId,
        region: 'us',
        sort: 'listen_score',
        safe_mode: 0,
      }).then((response) => {
        const data = response.data;
        // setChannels(data.podcasts);
        // setGenre(data.name);
        console.log(data);
      }).catch((error) => {
        console.log(error);
      });
    }
    fetchPodcastsByGenre();
  }, [genreId]);

  return (
    <Grid>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <Typography sx={{ fontSize: '20px' }}>Best In {genre}</Typography>
        <IconButton>
          <ArrowRightIcon sx={{ fontSize: '40px' }} />
        </IconButton>
      </div>
      <ShortPodcastList channels={channels} />
    </Grid>
  );
};

GetBestChannelsByGenre.propTypes = {
  genreId: PropTypes.string.isRequired,
};

export default GetBestChannelsByGenre;
