const GenreReducer = (
  state = {
    genres: {},
  },
  action = { type: '', payload: null },
) => {
  switch (action.type) {
    case 'GET_CHANNELS_BY_GENRE': {
      const { id, name, channels } = action.payload;
      return {
        ...state,
        genres: {
          ...state.genres,
          [id]: {
            name,
            channels,
          },
        },
      };
    }
    default:
      return state;
  }
};



