import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Divider from '@mui/material/Divider';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import { getEpisodesByChannel } from '../../redux/channels/actions/channels';
import IndividualEpisodeCard from '../episodes/IndividualEpisodeCard';
import GetEpisodesByChannel from './GetEpisodesByChannel';
import { MAIN_LIGHT_GREY } from '../shared/Constant';

const EpisodeList = (props) => {
  const { channelName } = props;
  console.log(channelName);
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const channel = useSelector(state => state.channels[channelName]) || {};
  const episodes = channel.episodes || [];
  const hearted = channel.hearted;

  useEffect(() => {
    dispatch(getEpisodesByChannel(channelName));
  }, [channelName]);

  return (
    <div>
      <Grid
        container
        sx={{
          padding: '3%',
          backgroundColor: MAIN_LIGHT_GREY,
        }}
      >
        <Grid item xs={12} container sx={{ marginBottom: '3%' }}>
          <Grid xs={3}><img src={channel.thumbnail} /></Grid>
          <Grid xs={9}>
            <Typography
              sx={{
                fontSize: '40px',
                fontWeight: 'bold',
                color: '#665A48',
              }}
            >
              {channelName}
            </Typography>
            <span>
            {(hearted) ? <FavoriteIcon /> : <FavoriteBorderIcon />}
            </span>
          </Grid>
        </Grid>
        <Grid xs={12}>
          <Divider />
        </Grid>
        {/* <Grid item xs={12} container justifyContent="center">
          {episodes && Array.isArray(episodes) ? 
            episodes.map((ep) => (
              <Grid item xs={12}>
                <IndividualEpisodeCard episode={ep} />
              </Grid>
            )) :
            <Typography
              sx={{}}
            >
              {episodes}
            </Typography>
          }
        </Grid> */}
        <GetEpisodesByChannel channel={channelName} />
      </Grid>
    </div>
  )
}

EpisodeList.propTypes = {
  channelName: PropTypes.string.isRequired,
};

export default EpisodeList
