import React from "react";
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import ActiveButton from "./ActiveButton";
import { Link } from 'react-router-dom';
import photo from '../Assets/Images/photo.jpg';

const Banner = () => {
  return (
    <Grid container sx={{ height: '100vh', alignItems: 'center', marginLeft: '500px' }}>
      <Grid item xs={5}>
        <Typography
          variant="h1"
          sx={{
            fontSize: '80px',
            color: '#665A48',
            fontWeight: 'bold',
            mb: 2
          }}
        >
          PODCASITY
        </Typography>
        <Typography
          variant="h2"
          sx={{
            fontSize: '50px',
            color: 'rgba(102, 90, 72, 0.75)',
            fontWeight: 'bold',
            mb: 3
          }}
        >
          The city of Podcast Lovers
        </Typography>
        <div style={{ display: 'flex' }}>
          <Link to="/dashboard">
            <ActiveButton text='Start Discovering' />
          </Link>
          <Link to="/signin">
            <ActiveButton text='Sign In' />
          </Link>
        </div>
      </Grid>
      <Grid item xs={7} sx={{ position: 'relative' }}>
        {/* <img
          src={photo}
          style={{
            height: '40vh',
          }}
        /> */}
        <Box
          sx={{
            height: '35vh',
            width: '35vh',
            backgroundColor: '#9F8772',
            position: 'absolute',
            zIndex: 2,
          }}
        />
        <Box
          sx={{
            height: '35vh',
            width: '35vh',
            backgroundColor: '#C8DBBE',
            position: 'absolute',
            zIndex: 1,
            left: '30px',
          }}
        />
      </Grid>
    </Grid>
  )
}

export default Banner;
