import React, { useState, useEffect } from 'react';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Button from '@mui/material/Button';
import ActiveButton from './ActiveButton';
import logo from '../Assets/Images/Podcasity.png';
import { Link } from 'react-router-dom';
import firebase from '../services/firebase';
import axios from 'axios';
import { authenticateUser } from '../actions/auth.js';

const AuthorizedNavBar = ({ children }) => {
  const [currUser, setCurrUser] = useState({});
  const getUser = async() => {
    try {
      const token = await firebase.auth.currentUser.getIdToken(true);
      const req = await axios.get("http://localhost:4000/users", {
        headers: {
          authorization: `Bearer ${token}`
        }
      });
      if (req.data) {
        setCurrUser(req.data);
      }
    } catch (error) {
      console.log(error);
    }
  }

  const logOut = async () => {
    await firebase.auth.signOut();
  }

  useEffect(() => {
    getUser();
  }, []);

  return (
    <div>
      <Grid container justifyContent="space-between" alignItems="center">
        <Grid item>
          <Link to="/dashboard">
            <img src={logo} style={{ height: '12vh' }} alt="Logo" />
          </Link>
        </Grid>
        <Grid item>
          <Button onClick={logOut}>
            Log Out
          </Button>
          <Typography>logged in</Typography>
        </Grid>
      </Grid>
      {children}
    </div>
  );
};

export default AuthorizedNavBar;
