const GenreReducer = (
  state = {
    genres: {},
  },
  action = { type: '', payload: null },
) => {
  switch (action.type) {
    case 'GET_CHANNELS_BY_GENRE': {
      const { genreName, channels } = action.payload;
      return {
        ...state,
        genres: {
          ...state.genres,
          [genreId]: {
            genreName,
            channels,
          },
        },
      };
    }
    default:
      return state;
  }
};

export default GenreReducer;
