import React from "react";
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import ActiveButton from "./ActiveButton";
import { Link } from 'react-router-dom';
import photo from '../Assets/Images/photo.jpg';

const Banner = () => {
  return (
    <Grid container sx={{ height: '100vh', marginLeft: '500px' }}>
      <Grid item xs={5}>
        <Typography
          sx={{
            fontSize: '80px',
            color: '#665A48',
            fontWeight: 'bold'
          }}
        >
          PODCASITY
        </Typography>
        <Typography
          sx={{
            fontSize: '50px',
            color: 'rgb(102,90,72,0.75)',
            fontWeight: 'bold',
            marginTop: '-20px'
          }}
        >
          The city of Podcast Lovers
        </Typography>
        <div style={{ display: 'flex', marginTop: '20px' }}>
          <Link to="/dashboard">
            <ActiveButton text='Start Discovering' />
          </Link>
          <Link to="/signin">
            <ActiveButton text='Sign In' />
          </Link>
        </div>
      </Grid>
      <Grid item xs={7} sx={{ position: 'relative' }}>
        {/* <img
          src={photo}
          style={{
            height: '40vh',
          }}
        /> */}
        <Box
          sx={{
            height: '35vh',
            width: '35vh',
            backgroundColor: '#9F8772',
            position: 'absolute',
            zIndex: 2,
            top: '10vh'
          }}
        />
        <Box
          sx={{
            height: '35vh',
            width: '35vh',
            backgroundColor: '#C8DBBE',
            position: 'absolute',
            zIndex: 1,
            left: '20vh',
          }}
        />
      </Grid>
    </Grid>
  )
}

export default Banner;
