import axios from 'axios';

const url = 'http://localhost:4000/fav_channels';

export const likeChannel = (channel, user) => axios.post(`${url}`, { channel, user });
export const unlikeChannel = (channel, user) => axios.delete(`${url}`, { channel, user });
