import axios from "axios";

const url = 'http://localhost:4000/channels';

export const addHeart = (channelId, userId) => axios.post(`${url}/heart`, {channelId, userId});
export const removeHeart = (channelId, userId) => axios.post(`${url}/unheart`, {channelId, userId});
export const getEpisodesByChannel = (channelName) => axios.get(`${url}/${channelName}`);
