import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { getChannelsByGenreName } from '../actions/genres.js'
import IndividualChannelCard from './IndividualChannelCard';

const GetBestChannelsByGenreName = (props) => {
  const { genreName } = props;
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      dispatch(getChannelsByGenreName(genreName));
    };
    fetchData();
  }, [genreId]);

  const genreData = useSelector((state) => state.genres[genreId]);
  const { channels, name } = genreData || {};

  useEffect(() => {
    dispatch(setChannels(channels));
  }, [name]);

  return (
    <Grid>
      {channels && <ShortPodcastList channels={channels} />}
    </Grid>
  );
};

GetBestChannelsByGenreName.propTypes = {
  genreId: PropTypes.string.isRequired,
};

export default GetBestChannelsByGenreName;
