import React, { useState } from 'react';
import PropTypes from 'prop-types';
import Button from '@mui/material/Button';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import Grid from '@mui/material/Grid';
import { useDispatch, useSelector } from 'react-redux';
import { addHeart, removeHeart } from '../../actions/channels.js';
import useAuthentication from '../CurrentUser';

const styles = {
  root: {
    padding: '10%',
    height: '30vh',
    marginBottom: '15%',
    backgroundColor: 'rgb(159, 135, 114, 0.3)',
    width: '100%',
  },
  img: {
    width: '20vh',
    objectFit: 'cover',
  },
  card: {
    textAlign: 'center',
  },
  titleContainer: {
    marginTop: '5px',
  },
  title: {
    fontSize: '18px',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
  },
};

const IndividualChannelCard = (props) => {
  const { channel } = props;
  const { id, title, thumbnail } = channel;
  const dispatch = useDispatch();
  const currentUser = useAuthentication();
  const [heart, setHeart] = useState({});

  const state = useSelector((state) => state);

  const handleHeartClick = () => {
    console.log(state);
    const heartAdded = state.channels.id;
    console.log(heartAdded);
    setHeart({ id: heartAdded });
    console.log(heart);
    // if (heartAdded.id) {
    //   dispatch(removeHeart(id, currentUser.id));
    // } else {
    //   dispatch(addHeart(id, currentUser.id));
    // }
  };

  return (
    <Grid sx={styles.root}>
      <Grid item sx={styles.card}>
        <img src={thumbnail} style={styles.img} alt="" />
      </Grid>
      <Grid item sx={styles.titleContainer}>
        <div style={styles.title}>{title}</div>
      </Grid>
      <Grid item>
        <Button onClick={handleHeartClick}>
          {heart.id ? <FavoriteIcon /> : <FavoriteBorderIcon />}
        </Button>
      </Grid>
    </Grid>
  );
};

IndividualChannelCard.propTypes = {
  channel: PropTypes.object.isRequired,
};

export default IndividualChannelCard;
