import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Divider from '@mui/material/Divider';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import AddCircleOutlineOutlinedIcon from '@mui/icons-material/AddCircleOutlineOutlined';
import { useDispatch, useSelector } from 'react-redux';
import { MAIN_DARK_BLUE, MAIN_GREEN, MAIN_LIGHT_GREY, MAIN_ORANGE } from '../shared/Constant.js';
import { useAuthentication, useFavouriteEpisodes } from '../../helpers/CurrentUser';
import { addHeart, removeHeart } from '../../redux/episodes/actions/episodes.js';
import { addToFavouriteEpisodes, removeFromFavouriteEpisodes } from '../../redux/auth/actions/auth.js';

const styles = {
  root: {
    marginBottom: '5%',
    backgroundColor: MAIN_LIGHT_GREY,
    width: '100%',
    borderRadius: '5px',
  },
  img: {
    width: '80%',
    objectFit: 'cover',
    borderRadius: '5px'
  },
  title: {
    fontSize: '18px',
    color: MAIN_DARK_BLUE,
    fontWeight: 'bold'
  },
  heart: {
    color: MAIN_ORANGE,
    fontSize: '18px'
  },
  addBtn: {
    color: MAIN_GREEN,
    fontSize: '18px'
  },
  box: {
    backgroundColor: MAIN_ORANGE,
    position: 'absolute',
    left: '1%',
    top: '3%',
    width: '100%',
    height: '100%',
    zIndex: -1,
    borderRadius: '5px'
  },
  btnContainer: {
    marginTop: '10px'
  },
  button: {
    cursor: 'pointer',
  },
};

const IndividualEpisodeCard = (props) => {
  const { episode } = props;
  const { id, thumbnail, title } = episode;
  const [heart, setHeart] = useState(false);

  const favouriteEpisodes = useSelector(state => state.auth.favouriteEpisodes);
  const hearted = favouriteEpisodes.some(item => item.id === id);

  const dispatch = useDispatch();
  const currentUser = useAuthentication();

  const handleHeartClick = () => {
    if (heart || hearted) {
      setHeart(false);
      dispatch(removeHeart(id, currentUser.id));
      dispatch(removeFromFavouriteEpisodes(episode));
    } else {  
      setHeart(true);
      dispatch(addHeart(id, currentUser.id));
      dispatch(addToFavouriteEpisodes(episode));
    }
  }

  return (
    <div>
      <Grid container sx={styles.root}>
        <Grid item xs={2}>
          <img src={thumbnail} style={styles.img} alt="" />
        </Grid>
        <Grid item xs={10}>
          <Typography sx={styles.title}>{title}</Typography>
          <span onClick={handleHeartClick} style={styles.button}>
            {(heart) ? <FavoriteIcon style={styles.heart} /> : <FavoriteBorderIcon style={styles.heart} />}
          </span>
          <span style={{ marginLeft: '10%' }}>
            <AddCircleOutlineOutlinedIcon style={styles.addBtn}/>
          </span>
        </Grid>
      </Grid>
      <Divider />
    </div>
  )
}

IndividualEpisodeCard.propTypes = {
  episode: PropTypes.string.isRequired,
};

export default IndividualEpisodeCard
