import React from 'react';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import { Link } from 'react-router-dom';
import { MAIN_LIGHT_YELLOW, MAIN_DARK_BLUE } from '../shared/Constant';

const GenreItem = (props) => {
  const { genreName } = props;

  return (
    <div
      style={{
        position: 'relative',
        zIndex: 1,
      }}
    >
     <Box
      sx={{
        backgroundColor: MAIN_DARK_BLUE,
        borderColor: 'white',
        position: 'absolute',
        top: '5%',
        width: '100%',
        height: '100%',
        zIndex: -1,
        borderRadius: '5px'
      }}
     />
      <Link
      to={`/${genreName}`}
      >
        <Button
          sx={{
            backgroundColor: MAIN_LIGHT_YELLOW,
            color: MAIN_DARK_BLUE,
            width: '25vh',
            height: '8vh',
            border: '1px solid #3D405B',
            borderRadius: '5px',
          }}
        >
        <Typography>
          {genreName}
        </Typography>
        </Button>
      </Link>
    </div>
  )
}

export default GenreItem;
