const initialState = {
  channels: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'GET_EPISODES_BY_CHANNEL': {
      const { title, episodes, message } = action.payload;
      return {
        ...state,
        [title]: episodes || message,
      }
    }
    case 'GET_CHANNEL_INFO_BY_NAME' : {
      const { title, publisher, thumbnail } = action.payload;
      return {
        ...state,
        [title]: {
          publisher: publisher,
          thumbnail: thumbnail,
        }
      }
    }
    default:
      return state;
  }
}

export default reducer;
