import * as api from '../../api/fav_channels';

export const likeChannel = (channel, user) => async (dispatch) => {
  try {
    await api.likeChannel(channel, user);
    dispatch({ TYPE: 'ADD_TO_FAVOURITES', payload: channel });
  } catch (error) {
    console.log(error);
  }
};

export const unlikeChannel = (channel, user) => async (dispatch) => {
  try {
    await api.unlikeChannel(channel, user);
    dispatch({ TYPE: 'REMOVE_FROM_FAVOURITES', payload: channel });
  } catch (error) {
    console.log(error);
  }
};
