import * as api from '../api/channels';

export const addHearToChannel = (channelId, userId) => async(dispatch) => {
  try {
    await api.addHearToChannel(channelId, userId);
    dispatch({ type: 'ADD_HEART', payload: channelId });
  } catch (error) {
    console.log(error);
  }
};
