import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import AuthorizedRoutes from './routes/AuthorizedRoutes.js';
import UnauthorizedRoutes from './routes/UnauthorizedRoutes.js';
import firebase from './services/firebase.js';

const App = () => {
  const [loading, setLoading] = useState(true);
  const dispatch = useDispatch();
  const authorized = useSelector((state) => state.auth.authorized);

  useEffect(() => {
    const authStateListener = firebase.auth.onAuthStateChanged(async (user) => {
      if (user) {
        setLoading(false);
        dispatch({ type: 'SET_AUTHORIZED' });
      } else {
        setLoading(false);
        dispatch({ type: 'SET_UNAUTHORIZED' });
      }
    });

    return () => {
      authStateListener();
    };
  }, [dispatch]);

  console.log(firebase.auth.currentUser.getIdToken(true));

  return (
    <div>
      {loading ? (
        <p>Loading...</p>
      ) : authorized ? (
        <AuthorizedRoutes />
      ) : (
        <UnauthorizedRoutes />
      )}
    </div>
  );
};

export default App;
