import axios from 'axios';

const url = 'http://localhost:4000/fav_episodes';

export const likeEpisode = (episode, user) => axios.post(`${url}`, { episode, user });
export const unlikeEpisode = (episode, user) => axios.delete(`${url}?episode=${episode}&user=${user}`);
