export default (genres = {}, action) => {
  switch (action.type) {
    case 'GET_CHANNELS_BY_GENRE': {
       const { id, name, channels } = action.payload;
       return {
        ...genres,
        [id]: {
          name: name,
          channels: channels,
        } 
       }
    }
    case 'SET_CHANNELS': {
      return action.payload;
    }
    default:
      return genres;
  }
}
