import React, { useState } from 'react';
import PropTypes from 'prop-types';
import Box from '@mui/material/Box';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import FavoriteIcon from '@mui/icons-material/Favorite';
import Grid from '@mui/material/Grid';
import { useDispatch } from 'react-redux';
import { addHeart, removeHeart } from '../../actions/channels.js';
import useAuthentication from '../CurrentUser';
import { LIGHT_GREEN, DARK_GREEN, MILD_GREY } from './Constant.js';

const styles = {
  root: {
    padding: '10%',
    height: '30vh',
    marginBottom: '15%',
    backgroundColor: '#F4F1DE',
    width: '100%',
    position: 'relative',
    zIndex: 0,
  },
  img: {
    width: '20vh',
    objectFit: 'cover',
  },
  card: {
    textAlign: 'center',
  },
  titleContainer: {
    marginTop: '5px',
  },
  title: {
    fontSize: '18px',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
  },
  button: {
    cursor: 'pointer',
  },
  heart: {
    color: DARK_GREEN,
  },
  box: {
    backgroundColor: 'rgba(0, 0, 0, 0.3)',
    position: 'absolute',
    right: '10%',
    bottom: '10%',
    width: '20%',
    height: '30%',
  },
};

const IndividualChannelCard = (props) => {
  const { channel } = props;
  const { id, title, thumbnail } = channel;
  const dispatch = useDispatch();
  const currentUser = useAuthentication();
  const [heart, setHeart] = useState(false);

  const handleHeartClick = () => {
    if (heart) {
      setHeart(false);
      dispatch(removeHeart(id, currentUser.id));
    } else {
      setHeart(true);
      dispatch(addHeart(id, currentUser.id));
    }
  };

  return (
    <Grid sx={styles.root}>
      <Grid item sx={styles.card}>
        <img src={thumbnail} style={styles.img} alt="" />
      </Grid>
      <Grid item sx={styles.titleContainer}>
        <div style={styles.title}>{title}</div>
      </Grid>
      <Grid item>
        <span onClick={handleHeartClick} style={styles.button}>
          {heart ? <FavoriteIcon style={styles.heart} /> : <FavoriteBorderIcon style={styles.heart} />}
        </span>
      </Grid>
      <Box sx={styles.box} />
    </Grid>
  );
};

IndividualChannelCard.propTypes = {
  channel: PropTypes.object.isRequired,
};

export default IndividualChannelCard;
