import React from 'react';
import Grid from '@mui/material/Grid';
import { useDispatch, useSelector } from 'react-redux';
import { getFavouriteList } from '../../redux/auth/actions/auth';
import useAuthentication from '../authorized/CurrentUser';
import IndividualChannelCard from '../channels/IndividualChannelCard';

const GetFavouriteChannels = () => {
  const currentUser = useAuthentication();
  const dispatch = useDispatch();
  dispatch(getFavouriteList(currentUser.id));
  const list = useSelector(state => state.auth.favouriteList);

  return (
    <Grid container spacing={3}>
      {list && list.map((item) => (
        <Grid item sm={6} md={4} lg={2} key={item.id}>
          <IndividualChannelCard channel={item} />
        </Grid>
      ))}
    </Grid>
  )
}

export default GetFavouriteChannels
