import React from 'react';
import PropTypes from 'prop-types';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import ArrowBackIosIcon from '@mui/icons-material/ArrowBackIos';
import Link from '@mui/material/Link';
import { useNavigate } from 'react-router-dom';
import GetEpisodesByChannel from './GetEpisodesByChannel';
import { MAIN_LIGHT_YELLOW } from '../shared/Constant';

const EpisodeList = (props) => {
  const { channelName } = props;
  const navigate = useNavigate();

  return (
    <div>
    <Grid container spacing={2} c>
      <Grid item xs={5} container justifyContent="center">
        <Box
          sx={{
            display: 'flex',
            alignItems: 'center',
            marginBottom: '30px',
            position: 'fixed',
            padding: '1%',
            backgroundColor: MAIN_LIGHT_YELLOW,
          }}
        >
          <Link onClick={() => navigate(-1)} sx={{ cursor: 'pointer' }}>
            <ArrowBackIosIcon />
          </Link>
          <Typography
            sx={{
              fontSize: '20px',
              fontWeight: 'bold',
              color: '#665A48',
            }}
          >
            {channelName}
          </Typography>
        </Box>
      </Grid>
      <Grid item xs={7} container justifyContent="center">
        <GetEpisodesByChannel channelName={channelName}/>
      </Grid>
    </Grid>
    </div>
  )
}

EpisodeList.propTypes = {
  channelName: PropTypes.string.isRequired,
};

export default EpisodeList
