import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import KeyboardDoubleArrowDownIcon from '@mui/icons-material/KeyboardDoubleArrowDown';
import KeyboardDoubleArrowUpIcon from '@mui/icons-material/KeyboardDoubleArrowUp';
import IconButton from '@mui/material/IconButton';
import Button from '@mui/material/Button';
import ArrowRightIcon from '@mui/icons-material/ArrowRight';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { getChannelsByGenre, setChannels } from '../actions/genres.js'
import ShortPodcastList from './ShortPodcastList';

const GetBestChannelsByGenreName = (props) => {
  const { genreName } = props;
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchData = async () => {
      dispatch(getChannelsByGenre(genreId));
    };
    fetchData();
  }, [genreId]);

  const genreData = useSelector((state) => state.genres[genreId]);
  const { channels, name } = genreData || {};

  useEffect(() => {
    dispatch(setChannels(channels));
  }, [name]);

  return (
    <Grid>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <Typography sx={{ fontSize: '20px' }}>Best In {name}</Typography>
        <Link to={`/${name}`}>
          <IconButton>
            <ArrowRightIcon sx={{ fontSize: '40px' }} />
          </IconButton>
        </Link>
      </div>
      {channels && <ShortPodcastList channels={channels} />}
    </Grid>
  );
};

GetBestChannelsByGenreName.propTypes = {
  genreId: PropTypes.string.isRequired,
};

export default GetBestChannelsByGenreName;
