import React from 'react';
import PropTypes from 'prop-types';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import Grid from '@mui/material/Grid';
import { useDispatch, useSelector } from 'react-redux';
import { addHearToChannel } from '../../actions/channels.js';
import useAuthentication from '../CurrentUser';

const styles = {
  root: {
    padding: '10%',
    height: '30vh',
    marginBottom: '15%',
    backgroundColor: 'rgb(159, 135, 114, 0.3)',
    width: '100%',
  },
  img: {
    width: '20vh',
    objectFit: 'cover',
  },
  card: {
    textAlign: 'center',
  },
  titleContainer: {
    marginTop: '5px',
  },
  title: {
    fontSize: '18px',
    whiteSpace: 'nowrap',
    overflow: 'hidden',
    textOverflow: 'ellipsis',
  },
};

const IndividualChannelCard = (props) => {
  const { channel } = props;
  const { id, title, thumbnail } = channel;
  const dispatch = useDispatch();
  const currentUser = useAuthentication();

  const heartAdded = useSelector(state => state.channel.id);

  const clickHeart = () => {
    dispatch(addHearToChannel(id, currentUser.id));
  }

  return (
    <Grid sx={styles.root}>
      <Grid item sx={styles.card}>
        <img src={thumbnail} style={styles.img} />
      </Grid>
      <Grid item sx={styles.titleContainer}>
        <div style={styles.title}>{title}</div>
      </Grid>
      <Grid item>
        <FavoriteBorderIcon onClick={clickHeart}/>
      </Grid>
    </Grid>
  );
};

IndividualChannelCard.propTypes = {
  channel: PropTypes.object.isRequired,
};

export default IndividualChannelCard;
