import React, { useState } from 'react';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Typography } from '@mui/material';
import Button from '@mui/material/Button';
import { Link, useLocation, useNavigate } from "react-router-dom";
import { signInWithEmailAndPassword } from 'firebase/auth';
import firebase from '../services/firebase';
import { useDispatch, useSelector } from 'react-redux';

const Signin = () => {
  const [userData, setUserData] = useState({
    email: '',
    password: '',
  });
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const setAuthorized = dispatch({ type: 'SET_AUTHORIZED' })

  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  }

  const handleSignIn = async (e) => {
    e.prevenDefault();
    try {
      const user = await signInWithEmailAndPassword(
        firebase.auth,
        userData.email,
        userData.password,
      );
      if (user) {
        setAuthorized();
        navigate('/dashboard');
      }
    } catch (error) {
      console.log("There is an error when signing in");
    }
  }

  return (
    <Box
      sx={{
        textAlign: "center",
        padding: "30px",
      }}
    >
      <Typography sx={{ fontSize: '30px', marginBottom: '2vh' }}>Sign In</Typography>
      <Grid container spacing={3} justifyContent='center'>
        <Grid item xs={8}>
          <TextField
            label="Email*"
            name="email"
            placeholder="Enter your email"
            sx={{ width: '50vh' }}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={8}>
          <TextField
            label="Password*"
            name="password"
            placeholder="Enter your password"
            sx={{ width: '50vh' }}
          />
        </Grid>
        <Grid item xs={8}>
          <Button variant="outlined" onClick={(e) => handleSignIn(e)}>Sign In</Button>
        </Grid>
        <Grid item xs={8}>
          <Typography>Doesn't have an account?</Typography>
          <Button variant="outlined">
            <Link to="/register">Register instead</Link>
          </Button>
        </Grid>
      </Grid>
    </Box>
  )
}

export default Signin
