const initialState = {
  channels: [],
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case 'GET_EPISODES_BY_CHANNEL': {
      const { title, episodes, publisher, image, message } = action.payload;
      return {
        ...state,
        [title]: {
          episodes: episodes || message,
          publisher: publisher,
          thumbnail: image,
        }
      }
    }
    default:
      return state;
  }
}

export default reducer;
