import * as api from '../api/channels';

export const addHeart = (channelId, userId) => async(dispatch) => {
  try {
    await api.addHeart(channelId, userId);
    dispatch({ type: 'ADD_HEART', payload: channelId });
  } catch (error) {
    console.log(error);
  }
};

export const removeHeart = (channelId, userId) => async(dispatch) => {
  try {
    await api.removeHeart(channelId, userId);
    dispatch({ type: 'REMOVE_HEART', payload: channelId });
  } catch (error) {
    console.log(error);
  }
};

export const addToFavList = (channel, user) => async(dispatch) => {
  try {
    await api.addToFavList(channel, user);
  } catch (error) {
    console.log(error);
  }
};
