      });

      console.log(response);
    } catch (error) {
      console.log(error);
    }
  } 

  return (
    <Box
      sx={{
        textAlign: "center",
        padding: "30px"
      }}
    >
      <Typography>Register</Typography>
      <Grid container spacing={3} justifyContent='center'>
        <Grid item xs={8}>
          <TextField
