export default (genres = {}, action) => {
  switch (action.type) {
    case 'GET_CHANNELS_BY_GENRE': {
       console.log(action.payload);
       const { id, name, channels } = action.payload;
       return {
        ...genres,
        [id]: {
          name,
          channels,
        } 
       }
    }
    default:
      return genres;
  }
}
